# Step 1: Check Pulumi Installation
check_pulumi_installed() {
    if ! command -v pulumi &> /dev/null; then
        echo "‚ö†Ô∏è Pulumi is not installed."
        echo "üîó Please install Pulumi: https://www.pulumi.com/docs/iac/get-started/aws/begin/"
        exit 1
    fi
}

# Step 2: Show Pulumi Version
get_pulumi_version() {
    echo "üîç Pulumi version information:"
    pulumi about | head -n 4
    echo ""
}

# Step 3: Load .env file safely
load_env_file() {
    if [[ -f "$ENV_FILE" ]]; then
        echo "üìÇ Loading environment variables from $ENV_FILE..."
        set -a
        source "$ENV_FILE"
        set +a
    else
        echo "‚ö†Ô∏è No .env file found. A new one will be created if needed."
    fi
}

# Step 4: Check if a variable is already in .env
is_var_in_env_file() {
    local var_name="$1"
    grep -q "^$var_name=" "$ENV_FILE" 2>/dev/null
}

# Step 5: Check if a variable is already set in the environment or .env
is_var_set() {
    local var_name="$1"
    if [[ -n "${!var_name}" ]]; then
        return 0  # Set in the environment
    elif is_var_in_env_file "$var_name"; then
        return 0  # Found in .env
    else
        return 1  # Not found
    fi
}

# Step 6: Save variable to .env file
save_to_env_file() {
    local var_name="$1"
    local var_value="$2"

    # Ensure .env exists
    touch "$ENV_FILE"

    # Remove existing entry if present
    grep -q "^$var_name=" "$ENV_FILE" && sed -i "/^$var_name=/d" "$ENV_FILE"

    # Append new value (without quotes)
    echo "$var_name=$var_value" >> "$ENV_FILE"
    echo "‚úÖ Saved $var_name in $ENV_FILE"
}

# Step 6.5: Ask to Load System-Tests Requirements
ask_load_requirements() {
    spacer
    echo "üîß Do you want to load the system-tests requirements?"
    echo "This will execute: ./build.sh -i runner"
    read -p "Run this setup? (y/n): " load_choice
    if [[ "$load_choice" =~ ^[Yy]$ ]]; then
        echo "üöÄ Loading system-tests requirements..."
        ./build.sh -i runner
        if [[ $? -ne 0 ]]; then
            echo "‚ùå Error: Failed to load system-tests requirements. Please check the logs."
            exit 1
        fi
        echo "‚úÖ System-tests requirements loaded successfully."
    else
        echo "‚ö†Ô∏è Skipping system-tests requirements setup."
    fi
}

# Step 7: Prompt for missing environment variables
check_and_set_env_var() {
    local var_name="$1"
    local help_message="$2"

    if is_var_in_env_file "$var_name"; then
        echo "‚úÖ $var_name is already stored in $ENV_FILE."
    elif is_var_set "$var_name"; then
        echo "‚úÖ $var_name is already set in the environment."
        read -p "Do you want to save $var_name in $ENV_FILE? (y/n): " save_choice
        if [[ "$save_choice" =~ ^[Yy]$ ]]; then
            save_to_env_file "$var_name" "${!var_name}"
        fi
    else
        echo "‚ùì $var_name is missing."
        echo "‚ÑπÔ∏è $help_message"
        read -p "Enter value for $var_name: " user_value
        export "$var_name=$user_value"
        read -p "Do you want to save $var_name in $ENV_FILE? (y/n): " save_choice
        if [[ "$save_choice" =~ ^[Yy]$ ]]; then
            save_to_env_file "$var_name" "$user_value"
        fi
    fi
}

