# Key-pair selection
# Step: Ask to create AWS Key Pair or manually set it
create_key_pair() {
    spacer

    # Ask for key pair name
    read -p "Enter a name for your new AWS Key Pair: " key_name
    if [[ -z "$key_name" ]]; then
        echo "‚ùå Invalid name. Key pair creation aborted."
        return
    fi

    # Define PEM file path
    pem_path="$HOME/.ssh/${key_name}.pem"

    # Create key pair
    echo "üöÄ Creating AWS Key Pair: $key_name ..."
    aws-vault exec sso-sandbox-account-admin -- aws ec2 create-key-pair --key-name "$key_name" --output text > "$pem_path"

    if [[ $? -ne 0 ]]; then
        echo "‚ùå Failed to create AWS Key Pair. Please check AWS permissions and try again."
        exit 1
    fi

    # Clean up PEM file
    echo "üîß Cleaning up the PEM file..."

    # Run the Python script to clean the PEM file
    echo "üîß Cleaning up the PEM file using Python..."
    python utils/scripts/ssi_wizards/aws_onboarding_wizard_utils.py "$pem_path"

    # Verify the file is cleaned properly
    if [[ $? -ne 0 ]]; then
        echo "‚ùå Error: Python script failed to clean the PEM file. Keeping the original file."
        exit 1
    fi

    #Protect with pass
    ssh-keygen -p -f $pem_path

    # Set proper permissions
    chmod 400 "$pem_path"
    echo "üîí File permissions set to 0400."

    # Save values to .env
    save_to_env_file "ONBOARDING_AWS_INFRA_KEY_PATH" "$pem_path"
    save_to_env_file "ONBOARDING_AWS_INFRA_KEYPAIR_NAME" "$key_name"

    echo "‚úÖ AWS Key Pair created successfully!"
    echo "   üîπ Key Name: $key_name"
    echo "   üîπ PEM File: $pem_path"
    echo "   üîπ Permissions set to 0400 (read-only for owner)"
}

# Step: AWS Key Pair Selection
ask_for_key_pair_selection() {
    spacer
    echo "üîë AWS Key Pair Configuration"
    echo "You have three options:"
    echo "1Ô∏è‚É£ Use the **default AWS key-pair** (recommended if no special permissions are needed and you don't want to debug the virtual machines)."
    echo "2Ô∏è‚É£ Use your own custom key-pair (set manually)."
    echo "3Ô∏è‚É£ Generate a new key-pair automatically using this wizard."

    while true; do
        read -p "Select an option (1-3): " keypair_choice
        case "$keypair_choice" in
            1)  # Option 1: Use Default Key-Pair
                echo "‚úÖ Using the default AWS key-pair. No variables will be set."
                return ;;  # Exit function, do not set variables

            2)  # Option 2: Use Custom Key-Pair
                echo "üîç Using a custom key-pair. Please provide the following details:"
                check_and_set_env_var "ONBOARDING_AWS_INFRA_KEY_PATH" "Absolute path to your PEM file. Ensure correct file permissions (chmod 400)."
                check_and_set_env_var "ONBOARDING_AWS_INFRA_KEYPAIR_NAME" "Name of the key pair created in AWS Console."
                return ;;  # Exit function after setting variables

            3)  # Option 3: Generate Key-Pair Automatically
                create_key_pair
                return ;;  # Exit function after generating key-pair

            *)  # Invalid input
                echo "‚ùå Invalid choice. Please enter a number between 1 and 3."
                ;;
        esac
    done
}

