# Step 11: Ask for the test language
ask_for_test_language() {
    spacer
    if [[ -n "$TEST_LIBRARY" ]]; then
        echo "‚úÖ TEST_LIBRARY is already set to: ${TEST_LIBRARY}"
    else
        echo "üß™ Select the language you want to test:"
        echo "1) Java"
        echo "2) Node.js"
        echo "3) Python"
        echo "4) .NET"
        echo "5) Ruby"
        echo "6) PHP"

        while true; do
            read -p "Enter the number of your choice (1-6): " choice
            case $choice in
                1) TEST_LIBRARY="java"; break;;
                2) TEST_LIBRARY="nodejs"; break;;
                3) TEST_LIBRARY="python"; break;;
                4) TEST_LIBRARY="dotnet"; break;;
                5) TEST_LIBRARY="ruby"; break;;
                6) TEST_LIBRARY="php"; break;;
                *) echo "‚ùå Invalid choice. Please select a number between 1 and 6.";;
            esac
        done

        export TEST_LIBRARY
        echo "‚úÖ Selected test language: $TEST_LIBRARY"
    fi
}

# Call the function to ask for the test language
